// Lista de URLs da Amazon para monitorar
const amazonUrls = [
  "https://amzn.to/44TPsu4",  // Balance Bike Nathor
  // Adicione mais URLs aqui
];

// Configura√ß√£o do Karooma
const KAROOMA_API_BASE = "https://seu-projeto.replit.app";

// Preparar dados para processamento
const items = amazonUrls.map((url, index) => ({
  json: {
    amazonUrl: url,
    index: index,
    karoomaApiBase: KAROOMA_API_BASE,
    timestamp: new Date().toISOString()
  }
}));

return items;

// Extrair dados da p√°gina da Amazon
const html = $input.first().json.data;
const url = $input.first().json.amazonUrl;

// Fun√ß√£o para extrair texto entre tags
function extractBetween(text, start, end) {
  const startIndex = text.indexOf(start);
  if (startIndex === -1) return null;
  const endIndex = text.indexOf(end, startIndex + start.length);
  if (endIndex === -1) return null;
  return text.substring(startIndex + start.length, endIndex).trim();
}

// Fun√ß√£o para limpar texto
function cleanText(text) {
  return text ? text.replace(/\s+/g, ' ').trim() : '';
}

// Extrair t√≠tulo
const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
const title = titleMatch ? cleanText(titleMatch[1].replace(' : Amazon.com.br', '')) : '';

// Extrair pre√ßo atual
const priceMatches = html.match(/R\$\s*([\d,\.]+)/g) || [];
const currentPrice = priceMatches.length > 0 ? 
  parseFloat(priceMatches[0].replace('R$', '').replace(',', '.').trim()) : null;

// Extrair avalia√ß√£o
const ratingMatch = html.match(/(\d+[,\.]\d+)\s*de\s*5\s*estrelas/i);
const rating = ratingMatch ? parseFloat(ratingMatch[1].replace(',', '.')) : null;

// Extrair n√∫mero de avalia√ß√µes
const reviewMatch = html.match(/(\d+[\.,]?\d*)\s*avalia√ß√µes/i);
const reviewCount = reviewMatch ? parseInt(reviewMatch[1].replace(/[\.,]/g, '')) : null;

// Extrair imagem
const imageMatch = html.match(/"hiRes"\s*:\s*"([^"]+)"/i) || 
                  html.match(/"large"\s*:\s*"([^"]+)"/i);
const imageUrl = imageMatch ? imageMatch[1] : null;

// Mapear categoria baseado no t√≠tulo/URL
function mapCategory(title, url) {
  const titleLower = title.toLowerCase();
  if (titleLower.includes('bicicleta') || titleLower.includes('bike') || 
      titleLower.includes('brinquedo') || titleLower.includes('infantil')) {
    return 'familia';
  }
  if (titleLower.includes('casa') || titleLower.includes('cozinha') || 
      titleLower.includes('organiz')) {
    return 'casa';
  }
  if (titleLower.includes('beleza') || titleLower.includes('cuidado') || 
      titleLower.includes('cosm√©tic')) {
    return 'autocuidado';
  }
  if (titleLower.includes('sa√∫de') || titleLower.includes('vitamina') || 
      titleLower.includes('supplement')) {
    return 'saude';
  }
  if (titleLower.includes('eletr√¥nic') || titleLower.includes('computador') || 
      titleLower.includes('celular')) {
    return 'tecnologia';
  }
  return 'casa'; // default
}

// Gerar descri√ß√£o baseada no t√≠tulo
function generateDescription(title) {
  if (title.toLowerCase().includes('bicicleta') && title.toLowerCase().includes('infantil')) {
    return `${title}. Produto ideal para desenvolvimento motor e divers√£o das crian√ßas, com qualidade comprovada na Amazon.`;
  }
  return `${title}. Produto selecionado com base em avalia√ß√µes positivas e qualidade comprovada na Amazon.`;
}

const extractedData = {
  title: title,
  description: generateDescription(title),
  currentPrice: currentPrice,
  rating: rating,
  reviewCount: reviewCount,
  imageUrl: imageUrl,
  category: mapCategory(title, url),
  affiliateLink: url,
  featured: rating >= 4.5,
  inStock: true,
  extractedAt: new Date().toISOString(),
  karoomaApiBase: $input.first().json.karoomaApiBase
};

return [{ json: extractedData }];

// Formatar dados para API do Karooma
const data = $input.first().json;

const formattedProduct = {
  title: data.title.substring(0, 255), // Limitar t√≠tulo
  description: data.description.substring(0, 500), // Limitar descri√ß√£o
  category: data.category,
  imageUrl: data.imageUrl,
  currentPrice: data.currentPrice ? data.currentPrice.toString() : null,
  originalPrice: null, // Ser√° calculado se houver desconto
  discount: null,
  rating: data.rating ? data.rating.toString() : null,
  affiliateLink: data.affiliateLink,
  featured: data.featured || false,
  inStock: data.inStock
};

return [{
  json: {
    productData: formattedProduct,
    karoomaApiBase: data.karoomaApiBase,
    originalData: data
  }
}];

// Log de sucesso
const response = $input.first().json;
const originalData = $input.first().json.originalData;

console.log('‚úÖ Produto sincronizado com sucesso:');
console.log('T√≠tulo:', originalData.title);
console.log('Pre√ßo:', originalData.currentPrice);
console.log('Rating:', originalData.rating);
console.log('Categoria:', originalData.category);
console.log('Resposta API:', response);

return [{
  json: {
    status: 'success',
    product: originalData.title,
    price: originalData.currentPrice,
    rating: originalData.rating,
    timestamp: new Date().toISOString(),
    apiResponse: response
  }
}];

// Log de erro/dados inv√°lidos
const data = $input.first().json;

console.log('‚ùå Dados inv√°lidos ou incompletos:');
console.log('T√≠tulo:', data.title || 'VAZIO');
console.log('Pre√ßo:', data.currentPrice || 'VAZIO');
console.log('URL:', data.affiliateLink || 'VAZIO');

if (!data.title) {
  console.log('Erro: T√≠tulo n√£o encontrado na p√°gina');
}
if (!data.currentPrice || data.currentPrice < 10 || data.currentPrice > 5000) {
  console.log('Erro: Pre√ßo inv√°lido ou fora do range (R$ 10 - R$ 5000)');
}

return [{
  json: {
    status: 'error',
    reason: 'dados_invalidos',
    data: data,
    timestamp: new Date().toISOString()
  }
}];

// Relat√≥rio final consolidado
const allResults = $input.all();

const successCount = allResults.filter(item => item.json.status === 'success').length;
const errorCount = allResults.filter(item => item.json.status === 'error').length;
const totalCount = allResults.length;

const report = {
  timestamp: new Date().toISOString(),
  summary: {
    total: totalCount,
    success: successCount,
    errors: errorCount,
    successRate: totalCount > 0 ? ((successCount / totalCount) * 100).toFixed(1) + '%' : '0%'
  },
  details: allResults.map(item => ({
    status: item.json.status,
    product: item.json.product || 'N/A',
    price: item.json.price || 'N/A',
    rating: item.json.rating || 'N/A'
  }))
};

console.log('üìä RELAT√ìRIO FINAL - Karooma Product Sync');
console.log('=' .repeat(50));
console.log(`Total de produtos processados: ${report.summary.total}`);
console.log(`Sucessos: ${report.summary.success}`);
console.log(`Erros: ${report.summary.errors}`);
console.log(`Taxa de sucesso: ${report.summary.successRate}`);
console.log(`Data/Hora: ${report.timestamp}`);
console.log('=' .repeat(50));

if (report.summary.success > 0) {
  console.log('‚úÖ Produtos sincronizados com sucesso:');
  report.details
    .filter(item => item.status === 'success')
    .forEach(item => {
      console.log(`  ‚Ä¢ ${item.product} - R$ ${item.price} (${item.rating}‚≠ê)`);
    });
}

if (report.summary.errors > 0) {
  console.log('‚ùå Produtos com erro:');
  report.details
    .filter(item => item.status === 'error')
    .forEach(item => {
      console.log(`  ‚Ä¢ ${item.product || 'Erro na extra√ß√£o'}`);
    });
}

return [{ json: report }];
